import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  Image,
  StyleSheet,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
  ScrollView,
  TextInput,
} from "react-native";
import { useAuth } from "../contexts/authContext";
import { useRouter } from "expo-router";
import { Ionicons } from '@expo/vector-icons'; 

const Menu = () => {
  const { userData } = useAuth();
  const [name, setName] = useState("");
  useEffect(() => {
    console.log("User Data:", userData); // Log the entire userData object
    if (userData) {
      setName(userData.username);
      console.log("Username set:", userData.username); // Log the username
    } else {
      console.log("No user data available"); // Log if userData is null or undefined
    }
  }, [userData]); // Make sure to listen to userData changes
  

  // Sample users data (replace with your actual data fetching logic)
  const [users, setUsers] = useState([]);
  const router = useRouter();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  // Fetch users data (simulate with useEffect)
  useEffect(() => {
    // Replace this with your actual data fetching
    const fetchUsers = async () => {
      try {
        // Simulate network request
        const response = await new Promise((resolve) =>
          setTimeout(
            () =>
              resolve({
                ok: true,
                json: () =>
                  Promise.resolve([
                    { id: "1", name: "User One" },
                    { id: "2", name: "User Two" },
                    { id: "3", name: "User Three" },
                  ]),
              }),
            2000
          )
        );

        if (response.ok) {
          const data = await response.json();
          setUsers(data);
        } else {
          setError(true);
        }
      } catch (err) {
        setError(true);
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  // Menu items data
  const [menuItems, setMenuItems] = useState([
    {
      id: "1",
      name: "Bacon Egg and Cheese",
      imageUri:
        "data:image/jpeg;base64,",
      isAdded: false,
    },
    {
      id: "2",
      name: "Sausage Egg and Cheese",
      imageUri:
        "data:image/jpeg;base64,",
      isAdded: false,
    },
    {
      id: "3",
      name: "Egg and Cheese",
      imageUri:
        "data:image/jpeg;base64,",
      isAdded: false,
    },
    {
      id: "4",
      name: "BYO",
      imageUri:
        "data:image/jpeg;base64,",
      isAdded: false,
    },
    {
      id: "5",
      name: "HashBrowns",
      imageUri:
        "data:image/jpeg;base64,",
      isAdded: false,
    },
  ]);

  // Toggle add/remove from cart
  const toggleAdd = (id) => {
    setMenuItems((prevItems) =>
      prevItems.map((item) =>
        item.id === id ? { ...item, isAdded: !item.isAdded } : item
      )
    );
  };

  // Render each menu item
  const renderMenuItem = ({ item }) => (
    <View style={styles.menuItem}>
      <Image
        source={{ uri: item.imageUri }}
        style={styles.menuImage}
        resizeMode="cover"
      />
      <TouchableOpacity
        onPress={() => {
          if (item.name === "BYO") {
            router.push({
              pathname: "/featured/BYO",
            });
          } 
          else if (item.name === "Sausage Egg and Cheese")
           {
            router.push({
                pathname: "/featured/SEC",
              });
           }
           else if (item.name === "HashBrowns")
           {
            router.push({
                pathname: "/featured/hashBrown",
              });
           }
           else if (item.name === "Egg and Cheese")
           {
            router.push({
                pathname: "/featured/EC",
              });
           }
          
          else {
            router.push({
              pathname: "/featured/BEC",
            });
          }
        }}
      >
        <Text style={styles.menuText}>{item.name}</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.addButton}
        onPress={() => toggleAdd(item.id)}
      >
        <Text style={styles.addButtonText}>{item.isAdded ? "-" : "+"}</Text>
      </TouchableOpacity>
    </View>
  );

  // Render each user item
  const renderUserItem = ({ item }) => (
    <View style={styles.userItem}>
      <Text style={styles.username}>{item.name}</Text>
      <TouchableOpacity
        style={styles.venmoButton}
        onPress={() => {
          // Handle Venmo action here
          console.log(`Venmo ${item.name}`);
        }}
      >
        <Text style={styles.venmoButtonText}>Venmo</Text>
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={{ flex: 1, padding: 20, backgroundColor: "#fff" }}>
    <ScrollView showsVerticalScrollIndicator={false}>
      {/* Greeting Section */}
      <View style={{ marginTop: 60 }}>
        <Text style={{ marginBottom: 8, fontSize: 28, fontWeight: '700', color: '#800000' }}>
          Good Afternoon, {name}!
        </Text>
        <Text style={{ fontSize: 18, fontWeight: '500', color: '#555' }}>
          What are you craving today?
        </Text>
      </View>
  
      {/* Search Bar */}
      <View
        style={{
          flexDirection: 'row',
          alignItems: 'center',
          marginTop: 25,
          backgroundColor: '#F5F5F5',
          borderRadius: 20,
          paddingHorizontal: 20,
          height: 50,
          shadowColor: "#000",
          shadowOffset: { width: 0, height: 2 },
          shadowOpacity: 0.1,
          shadowRadius: 4,
          elevation: 3,
        }}
      >
        <Ionicons name="search" size={20} color="#888" style={{ marginRight: 10 }} />
        <TextInput
          style={{ flex: 1, fontSize: 16, color: '#333' }}
          placeholder="Search for bagels"
          placeholderTextColor="#888"
        />
      </View>
  
      {/* Hot Right Now Section */}
      <Text
        style={{
          fontSize: 22,
          fontWeight: '700',
          color: '#800000',
          marginTop: 35,
          marginBottom: 15,
        }}
      >
        Hot Right Now
      </Text>
      <ScrollView horizontal showsHorizontalScrollIndicator={false} style={{ marginBottom: 25, marginLeft: -20 }}>
        <FlatList
          data={menuItems}
          keyExtractor={(item) => item.id}
          renderItem={renderMenuItem}
          horizontal
          contentContainerStyle={{
            paddingHorizontal: 10,
          }}
          showsHorizontalScrollIndicator={false}
        />
      </ScrollView>
  
      {/* Menu Section */}
      <Text style={{ fontSize: 24, fontWeight: '700', color: '#800000', marginBottom: 10 }}>
        Menu
      </Text>
      <View style={{ height: 1, backgroundColor: "#E0E0E0", marginBottom: 20 }} />
  
      <FlatList
        data={menuItems}
        keyExtractor={(item) => item.id}
        renderItem={renderMenuItem}
        numColumns={2}
        contentContainerStyle={{
          justifyContent: 'space-between',
          paddingBottom: 20,
        }}
        columnWrapperStyle={{ justifyContent: 'space-between' }}
        showsVerticalScrollIndicator={false}
      />
  
      
  
      {/* Uncomment and style the Users List section if needed */}
      {/*
      <Text style={{ fontSize: 20, fontWeight: '600', color: '#D32F2F', marginBottom: 10 }}>
        All Users:
      </Text>
  
      {loading ? (
        <ActivityIndicator size="large" color="#D32F2F" style={{ marginTop: 20 }} />
      ) : error ? (
        <Text style={{ color: "red", textAlign: "center", marginTop: 20 }}>Failed to load users.</Text>
      ) : users.length === 0 ? (
        <Text style={{ textAlign: "center", color: "#666", marginTop: 20 }}>No users found.</Text>
      ) : (
        <FlatList
          data={users}
          keyExtractor={(item) => item.id}
          renderItem={renderUserItem}
          contentContainerStyle={{ paddingBottom: 20 }}
        />
      )
      */}
    </ScrollView>
  </View>
  

  );
};

